$(window).on("load",function(){new Chartist.Bar("#peak-circles",{labels:["W1","W2","W3","W4","W5","W6","W7","W8","W9","W10"],series:[[1,2,4,10,6,-2,-1,-4,-8,-2]]},{high:15,low:-15,axisX:{labelInterpolationFnc:function(e,a){return a%2==0?e:null}}}).on("draw",function(e){"bar"===e.type&&e.group.append(new Chartist.Svg("circle",{cx:e.x2,cy:e.y2,r:2*Math.abs(Chartist.getMultiValue(e.value))+5},"ct-slice-pie"))})});