$(window).on("load",function(){for(var e=[[],[],[],[],[]],r=new Rickshaw.Fixtures.RandomData(50),a=0;a<75;a++)r.addData(e);var n=new Rickshaw.Graph({element:document.getElementById("multiple-renderers"),renderer:"multi",height:500,dotSize:5,series:[{name:"temperature",data:e.shift(),color:"rgba(153,184,152, 0.6)",renderer:"stack"},{name:"heat index",data:e.shift(),color:"rgba(254,206,168, 0.6)",renderer:"stack"},{name:"dewpoint",data:e.shift(),color:"rgba(255,132,124, 0.5)",renderer:"scatterplot"},{name:"pop",data:e.shift().map(function(e){return{x:e.x,y:e.y/4}}),color:"rgba(232,74,95, 0.6)",renderer:"bar"},{name:"humidity",data:e.shift().map(function(e){return{x:e.x,y:1.5*e.y}}),renderer:"line",color:"rgba(108,91,123, 0.35)"}]});new Rickshaw.Graph.RangeSlider.Preview({graph:n,element:document.querySelector("#slider")});n.render();new Rickshaw.Graph.HoverDetail({graph:n});var t=new Rickshaw.Graph.Legend({graph:n,element:document.querySelector("#multiple-renderers-legend")});new Rickshaw.Graph.Behavior.Series.Highlight({graph:n,legend:t,disabledColor:function(){return"rgba(0, 0, 0, 0.2)"}}),new Rickshaw.Graph.Behavior.Series.Toggle({graph:n,legend:t})});